import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [input, setInput] = useState('');
  const [result, setResult] = useState(null);
  const [error, setError] = useState('');

  const handleInputChange = (e) => {
    setInput(e.target.value);
  };

  // Improved JSON validation function
  const isValidJSON = (string) => {
    try {
      JSON.parse(string);
      return true;
    } catch (e) {
      return false;
    }
  };

  const handleSubmit = async () => {
    if (!isValidJSON(input)) {
      setError('Invalid JSON input. Please correct it and try again.');
      setResult(null);
      return;
    }

    setError('');
    try {
      const response = await axios.post('http://localhost:5001/bfhl', JSON.parse(input));
      setResult(response.data);
      setError('');
    } catch (error) {
      setError('Error fetching results. Make sure the backend server is running.');
      console.error("Error details:", error.response || error);  // Log error details to debug
      setResult(null);
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1>BFHL Data Processor</h1>
      <div>
        <textarea
          value={input}
          onChange={handleInputChange}
          style={{ width: '300px', height: '100px', marginBottom: '10px' }}
          placeholder="Enter JSON data here"
        />
        <br />
        <button onClick={handleSubmit}>Submit</button>
      </div>
      {error && <div style={{ color: 'red', marginTop: '10px' }}>{error}</div>}
      {result && (
        <div style={{ marginTop: '20px', backgroundColor: '#f0f0f0', padding: '10px' }}>
          <h3>Result:</h3>
          <pre>{JSON.stringify(result, null, 2)}</pre>
        </div>
      )}
    </div>
  );
}

export default App;
